var ssCheckbox=function(){};ssCheckbox.config={tag:"ss-checkbox",template:'<label for="{{name}}"><content></content></label><input type="checkbox" name="{{name}}" /><span></span>',attributes:["name"],init:function(a){if("true"===a.getAttribute("value")){var b=a.find("input");a.addClass("checked"),b.setAttribute("checked",!0)}a.setVal=function(b){var c=a.find("input");c.checked=b,a.value=b,a.toggleClass("checked")},a.addListener("click",function(){a.setVal(!a.containsClass("checked"))})}},sandlestrap.register(ssCheckbox);